sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {

      return;
   }
var request=g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');
	if(newValue=='hr')
		{
			g_form.addOption('u_what_needed','hr1','Human Resourses1');
			g_form.addOption('u_what_needed','hr2','Human Resourses2');
			g_form.addOption('u_what_needed','other1','Other Category1');
		}
	else if(newValue=='legal')
		{
			g_form.addOption('u_what_needed','lr1','Legall 1');
			g_form.addOption('u_what_needed','lr2','Legall 2');
			g_form.addOption('u_what_needed','other2','Other Category2');
		}
	else if(newValue=='facilities')
		{
			g_form.addOption('u_what_needed','fl1','Facilities 1');
			g_form.addOption('u_what_needed','fl22','Facilities 2');
			g_form.addOption('u_what_needed','other3','Other Category3');
		}
	if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_what_needed', request);
	}
   //Type appropriate comment here, and begin script below

}
